pkg install -y qemu-guest-agent > /dev/null && echo 'qemu_guest_agent_enable="YES"\nqemu_guest_agent_flags="-d -v -l /var/log/qemu-ga.log"\n#virtio_console_load="YES"' > /etc/rc.conf.local && echo -e '#!/bin/sh\nsleep 3\nservice qemu-guest-agent onestart' > /usr/local/etc/rc.d/qemu-agent.sh && chmod +x /usr/local/etc/rc.d/qemu-agent.sh && service qemu-guest-agent onestart
